package boletintema1;

public class SalidaPorPantalla6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int n =17;
		int i =9;
		
		System.out.printf("Ambos valores: %d %d %n", n, i);
		
		int suma = n+i;
		System.out.printf("Su suma: %d %n", suma);
		
		int resta = n-i;
		System.out.printf("Su resta: %d %n", resta);
		
		int multiplicacion = n*i;
		System.out.printf("Su multiplicacion: %d %n", resta);
		
		float division = (float)n/ (float)i;
		System.out.printf("Su division: %f %n", division);
	}

}
